<?phpfunction loadMessages($login_id){        if(!is_numeric($login_id))	    return false;    $result = mysql_query("SELECT * FROM permissions WHERE user_id=$login_id AND status=0");    $count = mysql_num_rows($result);    if($count>0)	addMsg("note","You have <a href='javascript:load_page(\"settings\");'>$count drives</a> waiting approval");    $userInfo = getUser($login_id);    if(empty($userInfo['name']))	addMsg("note", "You did not <a href='javascript:load_page(\"account\");'> enter in your name</a>");    if($userInfo['reset_password'])	addMsg("note", "You need to <a href='javascript:load_page(\"account\");'>change your password</a> before you log out.");    if(isIE()){	addMsg("warning", "ClouThe does not support Microsoft Internet Explorer, you may experience issues while on this app.");    }    $result = mysql_query("SELECT * FROM folders WHERE name='recyclebin' AND parent_id=0 AND user_id=$login_id LIMIT 1");    if(mysql_num_rows($result) == 0){	$init_drives = array("recyclebin","public","private");	foreach($init_drives as $drive){	    $sql_name = cleanSQL($drive);	    $result = mysql_query("SELECT * FROM folders WHERE name='$sql_name' AND parent_id=0 AND user_id=$login_id");	    if(mysql_num_rows($result) == 0){		$data = array('name'=>$drive);		$data['user_id'] = $login_id;		$id = insertSQL('folders',$data);		$data = array('id' => $id, 'root_id'=>$id);		updateSQL('folders', $data);	    }	}    }}