<?php$user = getUser(login_id);$used = getUserQuota(login_id);$remain = $user['quota']-$used;$percent = min(100,floor(100*$used/$user['quota']));$percentRemain = 100 - $percent;?><h2>Account</h2><div class='box fleft' style='width:400px'>    <h3><img class='icon' src="src/img/icons/drive_web.png"> Storage</h3>    <div class='progressbar'><?php echo $percent;?></div>    <p>Used: <b><?php echo formatSize($used);?> (<?php echo $percent;?>%) / <b><?php echo formatSize($user['quota']);?></b></b> -    Remaining: <b><?php echo formatSize($remain);?> (<?php echo $percentRemain;?>%)</b></p></div><div class='box <?php if(!$user['name']) echo "box_highlight";?> fleft' style='width:400px'>    <h3><img class='icon' src="src/img/icons/user.png"> Basic Information</h3>    <form class="ajaxform" action="updateBasic()" id="form_account_basic">    <table class='spec_table' >    <tr><td>Name: </td><td><input type='text' name='name' size="40" value="<?php echo $user['name'];?>"></td></tr>    <tr><td>Email: </td><td><input type='text' name='email' size="40" value="<?php echo $user['email'];?>"></td></tr>    </table>    <input type="hidden" name="send" value="Update">    <a href="javascript:updateBasic();" class="button button-update ajaxsubmit">Update</a>    </form></div><div class='box <?php if($user['reset_password']) echo "box_highlight";?> fleft' style='width:400px'>    <h3><img class='icon' src="src/img/icons/key.png"> Change Password</h3>    <form class="ajaxform" action="updatePassword()" id="form_account_password">    <table class='spec_table'>    <?php if(!$user['reset_password']){ ?>	<tr><td>Current Password: </td><td><input type='password' name='current_password' size="20"></td></tr>    <?php } ?>    <tr><td>New Password: </td><td><input type='password' name='password1' size="20"></td></tr>    <tr><td>Repeat Password: </td><td><input type='password' name='password2' size="20"></td></tr>    </table>    <input type="hidden" name="send" value="Change Password">    <a href="javascript:updatePassword();" class="ajaxsubmit button button-update">Change Password</a>    </form></div>